declare module '@decentraland/SoundController' {
  /**
   * Automatically plays a sound.
   * @param src A path to the sound file relative to the scene directory
   * @param options Additional options shuch as `volume`, and `loop`
   */
  export function playSound(src: string, options?: { volume?: number; loop?: boolean }): Promise<void>

  /**
   * Pauses the active sound.
   */
  export function pauseSound(): Promise<void>

  /**
   * Removes the active sound.
   */
  export function removeSound(): Promise<void>

  /**
   * Returns true if a sound is being played
   */
  export function isPlaying(): Promise<boolean>
}
